import React, { useState, useEffect } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';
import { Bar, Doughnut } from 'react-chartjs-2';
import api from '../services/api';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const Dashboard = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchLatestSimulation();
  }, []);

  const fetchLatestSimulation = async () => {
    try {
      const response = await api.get('/simulation/history');
      if (response.data.length > 0) {
        setStats(response.data[0].results);
      }
    } catch (error) {
      console.error('Error fetching simulation data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (!stats) return <div>No simulation data available</div>;

  const deliveryData = {
    labels: ['On-time', 'Late'],
    datasets: [
      {
        data: [stats.onTimeDeliveries, stats.lateDeliveries],
        backgroundColor: ['#4CAF50', '#f44336'],
      },
    ],
  };

  const fuelCostData = {
    labels: ['Low Traffic', 'Medium Traffic', 'High Traffic'],
    datasets: [
      {
        label: 'Fuel Costs by Traffic Level',
        data: [
          stats.fuelCosts.byTrafficLevel.Low,
          stats.fuelCosts.byTrafficLevel.Medium,
          stats.fuelCosts.byTrafficLevel.High,
        ],
        backgroundColor: ['#81C784', '#FFB74D', '#E57373'],
      },
    ],
  };

  return (
    <div className="dashboard">
      <div className="stats-container">
        <div className="stat-card">
          <h3>Total Profit</h3>
          <p>₹{stats.totalProfit.toFixed(2)}</p>
        </div>
        <div className="stat-card">
          <h3>Efficiency Score</h3>
          <p>{stats.efficiencyScore.toFixed(2)}%</p>
        </div>
        <div className="stat-card">
          <h3>Total Fuel Cost</h3>
          <p>₹{stats.fuelCosts.total.toFixed(2)}</p>
        </div>
      </div>

      <div className="charts-container">
        <div className="chart">
          <h3>Delivery Performance</h3>
          <Doughnut data={deliveryData} />
        </div>
        <div className="chart">
          <h3>Fuel Costs by Traffic Level</h3>
          <Bar data={fuelCostData} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
